mapper.select:
SELECT roleID,name,roleNo,layer,parentID,hasChild,allowCount,note
FROM sys_role

    AND name = #{param.name:VARCHAR}
    AND roleID = #{param.roleID:INTEGER}

ORDER BY roleID

mapper.update:
update sys_role
name = #{pojo.name:VARCHAR}, roleNo = #{pojo.roleNo:VARCHAR}, layer = #{pojo.layer:INTEGER}, parentID = #{pojo.parentID:INTEGER}, hasChild = #{pojo.hasChild:BIT}, allowCount = #{pojo.allowCount:INTEGER}, note = #{pojo.note:VARCHAR},
where roleID = #{pojo.roleID:INTEGER}
